app.directive('wstIconSmall', function($compile,icons) {
return {
    transclude: true,
    scope: {
        onChangeIcon: '&',
        ngModel: '=',
        ngDisabled: '='
    },

   template: '<div class="btn-group">'
                    +'<span type="button"  class="input-group-addon icp icp-dd btn btn-primary dropdown-toggle iconpicker-element" data-selected="fa-car" data-toggle="dropdown" ng-disabled="{{ngDisabled}}" style="width:32px;height:32px; margin-bottom:0px;padding-top: 7px;padding-left: 2px;padding-right: 2px;">'
                        //+'<span class="caret"></span>'
                        +'<span class="fa {{selectedIcon}}" style="font-size: medium;"></span>'
                        +'<span class="sr-only">Toggle Dropdown</span>'
                    +'</span>'
                    +'<div class="dropdown-menu iconpicker-container {{dropSide}}"><div class="iconpicker-popover popover fade in inline" style="top: auto; right: auto; bottom: auto; left: auto; max-width: none;"><div class="arrow"></div><div class="popover-content"><div class="iconpicker">'
                      +'<div class="iconpicker-items">'
                          +'<a role="button" ng-click="changeIcon(icon)" class="iconpicker-item" ng-repeat="icon in icons"><i class="fa {{icon.value}}"></i></a>'
                    +'</div></div></div></div></div>'
            +'</div>',
    require: 'ngModel',
    // append
    replace: true,
    // attribute restriction
    restrict: 'E',
    // linking method
    compile: function (element, attrs) {

        return function ($scope, element, attrs, controller) {

          $scope.icons = icons.faList;

          $scope.selectedIcon = $scope.ngModel || 'fa-caret-down';

          $scope.$watch(attrs['ngModel'], function (v) {
            $scope.selectedIcon = $scope.ngModel;
          });

          $scope.changeIcon = function(icon)
          {

            if (icon.value != $scope.selectedIcon)
            {
              $scope.selectedIcon = icon.value;
              $scope.ngModel = icon.value;
              setTimeout(function() {
                $scope.$apply();
                if ($scope.onChangeIcon)
                    $scope.onChangeIcon();
                  }, 0);
            }
          }
        }
      }
    }
});

app.directive('wstIcon', function($compile,icons) {
return {
    transclude: true,
    scope: {
        onChangeIcon: '&',
        ngModel: '=',
        ngDisabled: '='
    },

   template: '<div class="input-group">'
                +'<div class="form-control" style="height:32px;padding:5px">'
                    +'<span style="height:25px;width: 25px;position: absolute;"><i class="fa {{selectedIcon}}" style="font-size: medium;"></i></span>'
                +'</div>'
                +'<span type="button"  class="input-group-addon icp icp-dd btn btn-primary dropdown-toggle iconpicker-element" data-selected="fa-car" data-toggle="dropdown" style="height:32px;" ng-disabled="{{ngDisabled}}" >'
                    +'<span class="caret"></span>'
                    +'<span class="sr-only">Toggle Dropdown</span>'
                +'</span>'
                +'<div class="dropdown-menu iconpicker-container {{dropSide}}"><div class="iconpicker-popover popover fade in inline" style="top: auto; right: auto; bottom: auto; left: auto; max-width: none;"><div class="arrow"></div><div class="popover-content"><div class="iconpicker">'
                  +'<div class="iconpicker-items">'
                      +'<a role="button" ng-click="changeIcon(icon)" class="iconpicker-item" ng-repeat="icon in icons"><i class="fa {{icon}}"></i></a>'
                +'</div></div></div></div></div>'
            +'</div>',
require: 'ngModel',
    // append
    replace: true,
    // attribute restriction
    restrict: 'E',
    // linking method
    compile: function (element, attrs) {

        return function ($scope, element, attrs, controller) {
          $scope.icons = icons.faFree;

//+'<a role="button" ng-click="changeIcon(icon)" class="iconpicker-item" ng-repeat="icon in icons"><i class="fa {{icon.value}}"></i></a>'


          $scope.$watch(attrs['ngModel'], function (v) {
            $scope.selectedIcon = $scope.ngModel;
          });


          $scope.changeIcon = function(icon)
          {

            if (icon != $scope.selectedIcon)
            {
              $scope.selectedIcon = icon;
              $scope.ngModel = icon;
                  setTimeout(function() {
                    $scope.$apply();
                    if ($scope.onChangeIcon)
                        $scope.onChangeIcon();
                      }, 0);
            }
          }
        }

      }
    }
});
